    def lengthOfLongestSubstring(self, s: str) -&gt; int:
            window_chars = set()
            n = len(s)
            left = 0
            max_len = 0
            for right in range(n):
                while s[right] in window_chars:
                    window_chars.remove(s[left])
                    left += 1
                window_chars.add(s[right])
                max_len = max(max_len, right - left + 1)
            return max_len
